queries.query:
PREFIX dlt: <https://w3id.org/DLTOntology#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?l WHERE {
  ?vulnerability a dlt:SmartContractVulnerability ;
                 rdfs:label ?l .
}

PREFIX dlt: <https://w3id.org/DLTOntology#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?law ?dltsystem WHERE {
  [ 
      dlt:isRegulatedBy [a dlt:Law; rdfs:label ?law] ;
      rdfs:label ?dltsystem 
  ]
}

PREFIX dlt: <https://w3id.org/DLTOntology#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?component ?dltsystem WHERE {
  ?sys a dlt:DLTSystem ;
             rdfs:label ?dltsystem ;
             dlt:hasComponent [rdfs:label ?component] .
}

PREFIX dlt: <https://w3id.org/DLTOntology#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?stdorgal ?standard WHERE {
  ?stdorga a dlt:StandardizationOrganization ;
           rdfs:label ?stdorgal ;
           dlt:createsStandard [rdfs:label ?standard] .
}

PREFIX dlt: <https://w3id.org/DLTOntology#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?dltsystem ?usecase WHERE {
  ?sys dlt:isSpecializedFor [a dlt:RecordKeeping ; rdfs:label ?usecase ] ;
       rdfs:label ?dltsystem .
}

PREFIX dlt: <https://w3id.org/DLTOntology#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?dltsystem ?usecase WHERE {
  ?sys dlt:isSpecializedFor [rdfs:label ?usecase] ;
       rdfs:label ?dltsystem .
}
